        int left=0;
        int right=nums.size()-1;
        while(left<right){
            int mid= left+ (right-left)/2;
            if(predicate(mid,nums,target)){
                right=mid;
            }
            else{
                left=mid+1;
            }
        }
        return nums[left]==target ? left:-1;
    }
