                return ind

            if nums[ind] >= nums[start]:
                if nums[start] <= target < nums[ind]:
                    end = ind - 1
                else:
                    start = ind + 1

            else:
                if nums[ind] < target <= nums[end]:
                    start = ind + 1
                else:
                    end = ind - 1


        return -1
