        int n =nums.size();
        int left = 0;
        int right = n-1;
        int mid;

        while(left<=right)
        {
            mid = (left+right)/2;

            if(nums[mid]==target)
            return mid;

            else if(nums[mid]>=nums[left])
            {
                if(target<=nums[mid] && target>=nums[left])
                right = mid-1;
                else
                left = mid+1;
            }
            else
            {
                if(target>=nums[mid]&& target<=nums[right])
                left = mid+1;
                else
                right = mid-1; 
            }   
        }
        return -1;
    }
};

