        int pivotIndex = pivot(nums, size);

        if (target == nums[pivotIndex]) {
            return pivotIndex;
        } else if (target >= nums[0] && target <= nums[pivotIndex - 1]) {
            return bin_search(nums, 0, pivotIndex - 1, target);
        } else {
            return bin_search(nums, pivotIndex, size - 1, target);
        }
    }
};


