    public static int search(int[] nums, int target){
        int pivot = findpiv(nums);
        System.out.println("pivot is"+pivot);
        if(pivot==-1){
            return regularbin(nums,target,0,nums.length-1);
        }
        if(nums[pivot]==target){
            return pivot;
        }
        if(target>=nums[0]){
            return regularbin(nums,target,0,pivot-1);
        }
