     int s = 0;
     int e = n-1;
     int mid = s +(e-s)/2;

    while(s<e){
        if(arr[mid] >= arr[0]){
            s = mid +1;
        }
        else{
            e = mid;
        }
        mid = s +(e-s)/2;
    }
    return s;
}

int binarySearch(vector<int> &arr,int s,int e, int k){
    int start = s;
    int end = e;
    int mid = start + (end - start)/2;

    while(start <= end){
        if(arr[mid] == k){
            return mid;
        }
        else if(k > arr[mid]){
            start = mid +1 ;
        }
        else if(k<arr[mid]){
            end = mid -1;
        }
        mid = start + (end - start)/2;
    }
    return -1;
}

