class Solution {
public:
    Node* cloneGraph(Node* node) {
        if(node==NULL){
            return NULL;
        }
        queue<Node*>q;
        unordered_map<Node*,Node*>m;
        Node* src=new Node(node->val);
        m[node]=src;
        q.push(node);
        while(!q.empty()){
            auto fn=q.front();
