class Solution {
    // map from node of original graph to cloned node in new graph
    Map<Node, Node> map = new HashMap<>();

    public Node cloneGraph(Node root) {
        if (root == null) return null;

        // if the clone of current node (ie, root) exists, return the clone
        if (map.containsKey(root)) return map.get(root);

        // else, create the clone and put it in the map
        ArrayList<Node> neighbors = new ArrayList<>();
        Node clone = new Node(root.val, neighbors);
        map.put(root, clone);

        // clone each neighbor and add it to the newly cloned node's neighbors
        for (Node neighbor : root.neighbors) {
            clone.neighbors.add(cloneGraph(neighbor));
        }

        // return the newly cloned node
        return clone;
    }
}
