class Solution {
    unordered_map<int,Node*> copiedNodes;
public:

    bool isVisited(int val){
        bool flag = ( copiedNodes.find(val) != copiedNodes.end() );
        return flag;
    }

    Node* cloneGraph(Node* node ) {

        // base cases
        if(node==nullptr)return node;
        if(node->neighbors.size()==0){
            return new Node(node->val);
        }

        // create new copy node
        Node* copyNode = new Node(node->val);
        copiedNodes.insert({node->val, copyNode});


        vector<Node*> copyNeighbors; // vector to hold neighbor nodes

         // explore neighbors nodes
        for(auto adj : node->neighbors){
            int v = adj->val;

            if(!isVisited(v)){

               copyNeighbors.push_back( cloneGraph(adj) );
            }else{
                copyNeighbors.push_back(copiedNodes[v]);
            }
        }

        copyNode->neighbors = copyNeighbors; 

        return copyNode;
    }
};
