class Solution {
public:
    Node* cloneGraph(Node* node) {
        if(!node) return node;
        unordered_map<Node*,Node*> m;
        Node* ans = new Node(node->val,{});
        m[node]=ans;
        queue<Node*> q;
        q.push(node);
        while(!q.empty()){
            Node* currNode=q.front();
            q.pop();
            vector<Node*> neigh = currNode->neighbors;
            for(auto x:neigh){
