class Solution {
public:
    Node* cloneGraph(Node* node) {
        if(node == NULL){   // If we are given nothing then we have to return NULL
            return NULL;
        }
        Node* front = new Node(node->val,{});
        unordered_map<Node*,Node*> mp;  // To store that I've already created that node and may not be able to create it again
        mp[node]=front;  // Gave it the node and value at node is front (It means that I've created the front for this node)
        queue<Node*> q;  // Queue is used to store the nodes whose neighbors may not be present
        q.push(node);  // Yeah node's neighbors are still not created so let's push it in our queue
        while(!q.empty()){
            Node* curr=q.front();
            q.pop();
            for(auto adj:curr->neighbors){  // Taking one neighbor by for-each loop
                if(!mp[adj]){  // Checking if that is alrady present aor not i.e. there is chance that maybe we already created that in the past
                    mp[adj]= new Node(adj->val,{});  // If it's not created then we need to make it right, and stored in our map. Now we know for this neighbor(adj) we have a node
                    q.push(adj);  // What's left for us to see, this neighbor's neighbors
                }
                mp[curr]->neighbors.push_back(mp[adj]);  // After checking this we just update our new created node's adjacency list i.e. neighbor.
            }
        }
        return front;  // At last we just return first node of our copy.
    }
};
