class Solution {
private:
    Node* dfs(Node* node,unordered_map<Node*,Node*>& ump){
        vector<Node*>neighbour;
        Node* clone = new Node(node->val);
        ump[node] = clone;
        for(auto it:node->neighbors){
            if(ump.find(it)==ump.end()){
                neighbour.push_back(dfs(it,ump));
            }
            else{
                neighbour.push_back(ump[it]);
            }

        }
        clone->neighbors = neighbour;
        return clone;
    }
public:
    Node* cloneGraph(Node* node) {
        if(node == nullptr)
            return node;
        unordered_map<Node*, Node*>ump;
        //To find the neighbours present or not in constant time
        if(node->neighbors.size()==0){
            Node * clone = new Node(node->val);
            return clone;
        }
        return dfs(node,ump);
    }
};
