class Solution {
    private Node dfs(Node node, Map<Node, Node> ump) {
        List<Node> neighbour = new ArrayList<>();
        Node clone = new Node(node.val);
        ump.put(node, clone);
        for(Node it:node.neighbors){
            if(!ump.containsKey(it)) {
                neighbour.add(dfs(it, ump));
            } else {
                neighbour.add(ump.get(it));
            }
        }
        clone.neighbors = neighbour;
        return clone;
    }

    public Node cloneGraph(Node node) {
        if(node == null) {
            return node;
        }
        Map<Node, Node> ump = new HashMap<>();
        if(node.neighbors.size() == 0) {
            Node clone = new Node(node.val);
            return clone;
        }
        return dfs(node, ump);
    }
}

