class Solution {
    public Node cloneGraph(Node node) {
        if(node == null)
            return node;
        Map<Node, Node> ump = new HashMap<>();
        //To find the neighbours present or not in constant time

        Queue<Node> qu = new LinkedList<>();
        //For bfs traversal
        ump.put(node, new Node(node.val, new ArrayList<>()));
        qu.offer(node);
        while(!qu.isEmpty()){
            Node curr = qu.poll();

            for(Node it : curr.neighbors){
                if(!ump.containsKey(it)){
                    ump.put(it, new Node(it.val, new ArrayList<>()));
                    qu.offer(it);
                }
                ump.get(it).neighbors.add(ump.get(curr));
            }
        }
        return ump.get(node);
    }
}


