public Node cloneGraph(Node node) {
        if(node == null)
            return null;
        Queue<Node> q = new LinkedList<>();
        Map<Node,Node> map = new HashMap<>();//map to store current node and its clone against it
        q.add(node);
        map.put(node,new Node(node.val)); //current node, created new node with val of current node for clone
        while(q.size()>0){
            Node temp = q.poll();
            for(Node neighbour : temp.neighbors){//provided in given data each node has list of its neighbours
                if(!map.containsKey(neighbour)){//check if neighbour is already added or not if not added
                    map.put(neighbour,new Node(neighbour.val));//add to map and create its clone node as well
                    q.add(neighbour);//add neighbour to q to get its neighbours
                }
                map.get(temp).neighbors.add(map.get(neighbour));//copy neighbours to curr node's clone's
            }   //neighbour list from neighbours of current node i.e. neighbour
        }
        return map.get(node);
    }
