      val copy = Node(node.`val`)
      nodes[node.`val`] = copy
      for (next in node.neighbors) {
        val nextcopy = dfs(next!!)
        copy.neighbors.add(nextcopy)
      }
      return copy
    }
    return dfs(node)
  }
}
