        NodeCopy newNode = new NodeCopy(root.val);
        oldToNew.put(root,newNode);
        newNode.left = copyRandomHelper(root.left);
        newNode.right = copyRandomHelper(root.right);
        newNode.random = copyRandomHelper(root.random);
        return newNode;
    }

    public NodeCopy copyRandomBinaryTree(Node root) {
