class Solution:
    def cloneGraph(self, node: 'Node') -> 'Node':
        if not node:
            return

		# keep visited nodes, key=original node, value=copied node
        visited = {}

        def copy_node(node):
            # If there are circles, we could visit same node twice. 
            # If we've already visited, just return it.
            if node in visited:
                return visited[node]

            # create a new Node with original val, without neighbors
            visited[node] = Node(val=node.val)

            # set copied neighbors
            visited[node].neighbors = [copy_node(neighbor) for neighbor in node.neighbors]

            return visited[node]

        return copy_node(node)

