class Solution {
public:
    void dfs(Node* curr, Node* node, vector<Node*>& visited){
        // curr - current orignal node
        // node - current clone node
        // visisted - visited array

        visited[node->val] = node;
        for(auto ele: curr->neighbors){  // for neighbors, traverse vals using og graph's node - curr
            if(visited[ele->val] == NULL){    // check if already not visited
                Node* newnode = new Node(ele->val);    // create new node
                (node->neighbors).push_back(newnode); // add to neighbors of current clone node
                dfs(ele, newnode, visited);          // repeat  
            }
            else{
                (node->neighbors).push_back(visited[ele->val]); // get value from the visited array
            }
        }
    }

    Node* cloneGraph(Node* node) {
        if(node==NULL)
            return NULL;

        vector<Node*> visited(101, NULL);  // we will use this array to check if node has already been created
        Node* copy = new Node();
        copy->val = node->val;  // create head node with value same as orignal graph's root
        visited[node->val] = copy; // add the first node to visited array

        // iterate through all the neighbors of start node
        for(auto curr: node->neighbors){  
            if(visited[curr->val] == NULL){    // check if already not visited
                Node* newnode = new Node(curr->val);    // create new node
                (copy->neighbors).push_back(newnode); // add to neighbors of current clone node
                dfs(curr, newnode, visited);          // repeat for start node's neighbours in dfs()
            }
            else{
                (copy->neighbors).push_back(visited[curr->val]); // get value from the visited array
            }
        }
        return copy;
    }
};
