class Solution:
    def __init__(self):
        self.vis = {}

    def cloneGraph(self, node: 'Node') -> 'Node':
        if not node:
            return None
        if node in self.vis:
            return self.vis[node]

        new_node = Node(node.val)
        self.vis[node] = new_node
