class Solution(object):
    def cloneGraph(self, node):
        """
        :type node: Node
        :rtype: Node
        """
        if not node:
            return
        stack = [node]
        visited = {}
        visited[node] = Node(node.val,[])
        while stack:
            curnode = stack.pop()
            for nei in curnode.neighbors:
                if nei not in visited:
                    visited[nei] = Node(nei.val,[])
                    stack.append(nei)
                #append all the neighbors whether it's visited or not
                visited[curnode].neighbors.append(visited[nei])
        return visited[node]

