class Solution {
public:
    Node* cloneGraph(Node* node) {
        if(node==nullptr) return node;
        unordered_map<Node*, Node*> ma;
        queue<Node*> q;
        Node *t = new Node(node->val);
        Node *head = t;
        q.push(node);
        ma[node] = t;

        while(q.size()){
            for(auto a: q.front()->neighbors)
                if(ma.count(a)==0){
                    t = new Node(a->val);
                    ma[a] = t;
                    q.push(a);
                }
            q.pop();
        }

        for(auto a: ma){
            t = a.second;
            for(auto b:a.first->neighbors)
                a.second->neighbors.push_back(ma[b]);
        }

        return head;
    }
};
