public Node cloneGraph(Node node) {
        if (node == null)
            return null;
        // map Node.val to clone Node
        HashMap<Integer, Node> map = new HashMap<>();

        return cloneGraph(map, node);
    }

// Overloaded function, recursive
private Node cloneGraph(HashMap<Integer, Node> map, Node node) {
		// if the node is already visited, return the reference of the cloned node
        if (map.containsKey(node.val))
            return map.get(node.val);

        Node clone = new Node(node.val);
		// put the clone Node in the map before making any recursive calls
        map.put(node.val, clone);

        ArrayList<Node> neighbors = new ArrayList<>();

		// traverse through the neighbors of original Node and call cloneGraph(HashMap, Node) method for every neighbor
        for (Node neighbor: node.neighbors) {
            neighbors.add(cloneGraph(map, neighbor));
        }

        clone.neighbors = neighbors;
        return clone;
}
