class Solution {
public:
    vector<int> sortArrayByParityII(vector<int>& nums) {
        vector<int> even;
        vector<int> odd;

        //seperating even and odd elements
        for(int i=0; i < nums.size(); i++)
        {
            if(nums[i] & 1)
            odd.push_back(nums[i]);
            else 
            even.push_back(nums[i]);
        }
        int k=0, m=0;
        inserting even element at even index and odd at odd index.
        for(int i=0; i < nums.size(); i++)
        {
            if(i&1)//odd
                nums[i]= odd[m++];
            else//even
                nums[i]= even[k++];
        }
        return nums;
    }
};
