    vector<int> sortArrayByParityII(vector<int>& nums) {

        int odd = 1; // pointer at odd index
        int even = 0; //pointer at even index 

        while(even<nums.size()){
            if(nums[even] % 2 == 0){ // if even place has even value 
                even += 2; // increment even pointer
            }
            else{ // else even place has odd value
                swap(nums[even], nums[odd]); // swap values such that odd pointer will surely have odd value
                odd += 2; // increment odd pointer
            }
        }
        return nums; // return your in-place solution 
    }
};
