impl Solution {
    pub fn sort_array_by_parity_ii(a: Vec<i32>) -> Vec<i32> {
        let mut even_ptr = 0;
        let mut odd_ptr = 1;
        let size = a.len();
        let mut res = a.clone();

        while even_ptr < size && odd_ptr < size {
            if a[even_ptr] % 2 == 0 {
                even_ptr += 2;
            } else if a[odd_ptr] % 2 != 0 {
                odd_ptr += 2;
            } else {
                res[even_ptr] = a[odd_ptr];
                res[odd_ptr] = a[even_ptr];

                even_ptr += 2;
                odd_ptr += 2;
            }
        }

        res
    }
}
