public int[] sortArrayByParityII(int[] array) {
    int len = 0;
    if(array == null || ((len = array.length) & 1) == 1){
        return null;
    }
    int even = 0;
    int odd = len - 1;
    while(true){
        while(even < len && (array[even] & 1) == 0){
            even += 2;
        }
        if(even == len){
            break;
        }
        while(odd > -1 && (array[odd] & 1) == 1){
            odd -= 2;
        }
        if(odd == -1){
            break;
        }
        swap(array, even, odd);
    }
    return array;
}
 private void swap(int[] array, int i, int j){
    int temp = array[i];
    array[i] = array[j];
    array[j] = temp;
}
