class Solution {
public:
    vector<int> arrayRankTransform(vector<int>& arr) 
    {
        if (arr.size() == 0) {
            return {};
        }
        if (arr.size() == 1) {
            return {1};
        }

        vector<pair<int,int>> v;
        for (int i = 0; i < arr.size(); i++) {
            v.push_back({arr[i], i});
        }

        sort(v.begin(), v.end());
        int rank = 1;
        int prev = v[0].first;
        v[0].first = rank;

        for (int i = 1; i < v.size(); i++) {
            if (v[i].first == prev) {
                prev = v[i].first;
                v[i].first = rank;
            }
            else {
                prev = v[i].first;
                v[i].first = ++rank;
            }
        }

        vector<int> res(arr.size(), 0);
        for (int i = 0; i < v.size(); i++) {
            res[v[i].second] = v[i].first;
        }
        return res;
    }
};
