	vector<int> arrayRankTransform(vector<int>& arr) {
		int n = arr.size();
		if(n == 0){
			return {};
		}
		vector<int> v;
		for(int i = 0; i < n; i++){
			v.push_back(arr[i]);
		}
		sort(v.begin(), v.end());
		unordered_map<int, int> m;
		m[v[0]] = 1;
		for(int i = 1; i < n; i++){
			if(v[i] == v[i-1]){
				m[v[i]] = m[v[i - 1]];
			}
			else{
				m[v[i]] = m[v[i - 1]] + 1;
			}
		}
		vector<int> res;
		for(int i = 0; i < n; i++){
			res.push_back(m[arr[i]]);
		}
		return res;
	}
};

