class Solution {
    func arrayRankTransform(_ arr: [Int]) -> [Int] {
        var dictionary = [Int : Int]()
        for case let element in arr {
            if let value = dictionary[element] {
                dictionary[element] = value + 1 
            } else {
                dictionary[element] = 1 
            }
        }
        var rankArray = dictionary.keys.sorted()
        var rankDict = [Int : Int]()
        var index = 0 
        while index < rankArray.count {
            rankDict[rankArray[index]] = index + 1
            index += 1
        }
        var result = [Int]()
        for case let element in arr {
            result.append(rankDict[element] ?? 1)
        }
        return result
    }
}
