class Solution {
public:
    vector<int> searchRange(vector<int>& nums, int target) {      
        auto lb = lower_bound(nums.begin(), nums.end(), target);
        // checks if the lower bound of target in vector is the target itself otherwise target does not exist in vector
        if(lb-nums.begin() == nums.size() || *lb != target)    return {-1, -1};

