        if(lb-nums.begin() == nums.size() || *lb != target)    return {-1, -1};

        auto ub = upper_bound(nums.begin(), nums.end(), target);
        int l = lb-nums.begin(), r = (ub-1)-nums.begin();
        return {l, r};
    }
