            while l < r:
                mid = (l + r) // 2
                if nums[mid] == target: r = mid
                else: l = mid + 1
            return l
        def searchright(l, r):
            while l <= r:
                mid = (l + r) // 2
                if nums[mid] == target: l = mid + 1
                else: r = mid - 1
            return r
