class Solution {
public:
    vector<int> searchRange(vector<int>& nums, int target) {
        vector <int> ans(2, -1);
        int n = nums.size();

        // Finding index of first occurrence
        int low = 0, high = n - 1;
        int mid;
        while (low <= high) {
            mid = (low + high) / 2;
            if (nums[mid] > target)
                high = mid - 1;
            else if (nums[mid] < target)
                low = mid + 1;  
            else {
                if (mid == 0 || nums[mid] != nums[mid - 1]) {
                    ans[0] = mid;
                    break;
                } else {
                    high = mid - 1;
                }    
            }          
        }

        // Finding index of last occurrence
        low = 0, high = n - 1;
        while (low <= high) {
            mid = (low + high) / 2;
            if (nums[mid] > target)
                high = mid - 1;
            else if (nums[mid] < target)
                low = mid + 1;  
            else {
                if (mid == n - 1 || nums[mid] != nums[mid + 1]) {
                    ans[1] = mid;
                    break;
                } else {
                    low = mid + 1;
                }    
            }          
        }
        return ans;
    }
};
```

