class Solution {
private:
    int m_findLow(vector<int> &nums, int target, int low, int high)
    {
        int mid;
        while (low <= high) {
            mid = low + (high-low)/2;
			//Advance low only if it is not pointing to target
            if( nums[mid] == target) {
                high = mid - 1;
            }
            else {
                low = mid + 1;
            }
        }
        return low;
    }

    int m_findHigh(vector<int> &nums, int target, int low, int high)
    {
        int mid;
        while (low <= high) {
            mid = low + (high-low)/2;
			//Advance high only if it is not pointing to target
            if( nums[mid] == target) {
                low = mid + 1;
            }
            else {
                high = mid - 1;
            }
        }
        return high;
    }

public:

    vector<int> searchRange(vector<int> &nums, int target)
    {
        int size = nums.size(), low = 0, high = size - 1, mid;
        vector<int> pos(2, -1);

        while (low <= high) {
            mid = low + (high - low) / 2;

			//First check if the target is present in the list at all
            if (nums[mid] == target) {
                if (nums[low] != target) {
					//Low should be in the lower half
                    low = m_findLow(nums, target, low, mid);
                }
                if (nums[high] != target) {
					//High should be in the upper half
                    high = m_findHigh(nums, target, mid, high);
                }
                pos[0] = low;
                pos[1] = high;
                break;
            }
            else if (nums[mid] > target && nums[high] != target) {
                high = mid - 1;
            }
            else if (nums[mid] < target && nums[low] != target) {
                low = mid + 1;
            }
        }
        return pos;
    }
};
```

