class Solution {
    public int[] searchRange(int[] nums, int x) {
        if (nums.length == 0) return new int[]{-1,-1};
        int start = 0;
        int end = nums.length - 1;
        while (start + 1 < end) {
            int mid = start + (end - start) / 2;
            if (nums[mid] < x) start = mid;
            else end = mid;
        }
        int left = nums[start] == x ? start : end;
        if (nums[left] != x) return new int[]{-1,-1};
        start = left;
        end = nums.length - 1;
        while(start + 1 < end) {
            int mid = start + (end - start) / 2;
            if (nums[mid] <= x) start = mid;
            else end = mid;
        }
        int right = nums[end] == x ? end : start;
        return new int[]{left,right};
    }
}
