class Solution {
public:
    vector<int> searchRange(vector<int>& nums, int t) {
        int n = nums.size();
        vector<int> ans(2, -1);

		// if there are no elements in nums
        if(n==0)    return ans;

		// initialized low(l), and high(r)
        int l=0, r = n-1, m;
        while(l<=r){
            m = l + (r-l)/2;
            if(nums[m]>=t)   r = m-1;
            else    l = m+1;
        }

		// here are three cases:
		// if 1st element is smaller than target, then l =0, r=-1, so we exit.
		// if last element is smaller than target, then l=n, and r=n-1, so we exit.
		// if first element is greater than target, then r=-1, and l =0, but first element is not target, so we exit.
        if((l!=0 && r==-1) || (l>=n && r==n-1) || nums[r+1]!=t)   return ans;

		// else the next index after r, is the first element for first occurence.
        ans[0] = r+1;

        l = 0, r = n-1;
        while(l<=r){
            m = l+(r-l)/2;
            if(nums[m]<=t)   l = m+1;
            else    r = m-1;            
        }

		// the last occurence may be the same as the first occurence or the last occurence.
        ans[1] = l-1;
        return ans;
	}
};
