vector<int> searchRange(vector<int>& nums, int t) {
	int n = nums.size();
	vector<int> ans(2, -1);
	// To check if the array is empty
	if(n==0)    return ans;
	// To check if array has only one element
	else if(nums.size()==1){
		if(nums[0]==t)			return {0, 0};
		return ans;
	}
	// If first element of array is our target
	if(t==nums[0])  ans[0] = 0;
	// Else we do binary search for the first element in array
	else{
		int low =0, high = n-1;
		while(low<high){
			int mid = (low+high)/2;
			if(nums[mid]==t && nums[mid-1]<nums[mid]){
				ans[0] = mid; break;
			}
			else if(nums[mid]>=t)   high = mid;
			else                    low = mid+1;
		}
	}
	// If last element if our target
	if(t==nums[n-1])  ans[1] = n-1;
	// Else we do binary search for the last element in array
	else{
		int low =0, high = n;
		while(low<high){
			int mid = (low+high)/2;
			if(nums[mid]==t && nums[mid]<nums[mid+1] && ans[0]!=mid){
				ans[1] = mid; break;
			}
			else if(nums[mid]<=t)   low = mid+1;
			else                    high = mid;
		}
	}
	// Checking the boundary cases
	if(ans[0]!=-1 && ans[1]==-1) 		ans[1]=ans[0];
	else if(ans[1]!=-1 && ans[0]==-1)		ans[0]=ans[1];
	return ans;
}
