**Logic is pretty simple:** Just perform Binary Search from left to right to find **FIRST** index and right to left to find **LAST** index

Time: O(log n)

**Upvote** my solution if you liked my approach :)

```
class Solution {
public:
    vector<int> searchRange(vector<int>& nums, int target) {

        int first_index = bs(nums, target);    // O(log n)
        int last_index = mod_bs(nums, target); // O(log n)

        return vector<int>{first_index, last_index};
    }

    // Modified Binary Search that searches in reverse direction
    int mod_bs(vector<int>& nums, int target)
    {
        int low, high, ind;
        low = nums.size()-1;
        high = 0;
        ind = -1;
        while (high <= low) 
        {
            int mid = high + (low - high)/2;

            int current = nums[mid];

            if (current == target) 
                ind = mid;

            if (current <= target) 
                high = mid + 1;
            else
                low = mid - 1;
        }

        return ind;
    }

    // Normal Binary Search
    int bs(vector<int>& nums, int target)
    {
        int low, high, ind;
        low = 0;
        high = nums.size()-1;
        ind = -1;

        while (low <= high) {
            int mid = low + (high - low)/2;

            int current = nums[mid];

            if (current == target)
                ind = mid;

            if (current < target)
                low = mid+1;
            else
                high = mid-1;
        }

        return ind;
    }
};
