class Solution {
public:
    vector<int> searchRange(vector<int>& nums, int target) {
        vector<int> res(2, -1);
        res[0] = _searchRange(nums, target, true);
        res[1] = _searchRange(nums, target, false);
        return res;
    }

    int _searchRange(vector<int>& nums, int target, bool first) {
        int ans = -1, l = 0, r = nums.size() - 1;
        while(l <= r) {
            int mid = (l + r) >> 1;

            if(nums[mid] > target) {
                r = mid - 1;
            } else if(nums[mid] < target) {
                l = mid + 1;
            } else {
                ans = mid;
                // find first index => search left
                if(first) {
                    r = mid - 1;
                } else {
                    l = mid + 1; // find last index => search right
                }
            }
        }
        return ans;
    }
};
