        int sum = 0;
        for(int i=0; i<n; i++){
            sum += (gas[i] - cost[i]);
            if(sum < 0){
                sum = 0;
                answer = i+1;
            }
        }
        return answer;
    }
    private int arraySum(int[] arr){
        int n = arr.length;
        int sum = 0;
        for(int i=0; i<n; i++){
            sum += arr[i];
        }
        return sum;
    }
}
