                carry += gas[i - 1] - cost[i - 1];
                if (carry < city_carry.second) {
                    city_carry = {i, carry};
                }
            }
            carry += gas[gas.size()-1] - cost[gas.size()-1];
            return carry >= 0 ? city_carry.first : -1;
        }
    };

