class Solution {
public:
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) {
        int n = gas.size();
        int sum=0, store=0, over=0;
        for(int i=0;i<n;++i){
            over+=gas[i]-cost[i]; //if total gas is less than cost then solution not exist
            sum+=gas[i]-cost[i]; //next two lines is basically the idea of kadane algorithm
            if(sum<0){store=i+1;sum=0;} //just use extra variable store to keep track of index to start
        }
        if(over<0) return -1;
        return store;
    }
};
