class Solution:
    def canCompleteCircuit(self, gas, cost):
        """
        :type gas: List[int]
        :type cost: List[int]
        :rtype: int
        """
        if sum(gas) < sum(cost):
            return -1
        tank = 0
        min_gas, min_gas_loc = 0, None
        for i in range(len(gas)):
            tank += gas[i] - cost[i]
            if tank < min_gas:
                min_gas = tank
                min_gas_loc = i
        return 0 if min_gas_loc is None else (min_gas_loc + 1) % len(gas)
```

```
impl Solution {
    pub fn can_complete_circuit(gas: Vec<i32>, cost: Vec<i32>) -> i32 {
        match gas
            .into_iter()
            .zip(cost.into_iter())
            .map(|(g, c)| g - c)
            .enumerate()
            .fold((0, (0, 0)), |(s, pass @ (_, vm)), (i, v)| match s + v {
                s if s < vm => (s, (i as i32 + 1, s)),
                s => (s, pass),
            }) {
            (s, _) if s < 0 => -1,
            (_, (im, _)) => im,
        }
    }
}
```

