Just do a brute force cyclic traversal from each index, but with a **small optimization**
![image](https://assets.leetcode.com/users/images/ec383eba-cbb5-4df9-be2c-c3c3b8a9b3e7_1673063411.3091168.jpeg)
![image](https://assets.leetcode.com/users/images/0b042bb6-6e6e-4f3a-b424-eabf54bec2b0_1673063421.4989889.jpeg)



```
class Solution {
public:
    int getBestReachableIdx(vector<int>&gas, vector<int>&cost, int startIdx)
    {
        int n = gas.size(), currIdx = (startIdx + 1) % n;
        int gasLeft = gas[startIdx] - cost[startIdx];
        if (gasLeft < 0) return -1;

        while(currIdx != startIdx)
        {
            gasLeft += gas[currIdx];

            int reqGas = cost[currIdx];
            if (gasLeft >= reqGas) gasLeft -= reqGas;
            else if (gasLeft < reqGas) break;   

            currIdx = (currIdx + 1) % n;
        }
        return currIdx;
    }
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) 
    {
        int n = gas.size();
        int currIdx = 0;
        while(currIdx < n)
        {
            int bestReachableIdx = getBestReachableIdx(gas, cost, currIdx);
            if (bestReachableIdx == currIdx) return currIdx;
            else if (bestReachableIdx > currIdx) currIdx = bestReachableIdx + 1; //optimization step
            else if ((bestReachableIdx == -1) || (bestReachableIdx < currIdx))  currIdx++;
        }
        return -1;

    }
};
```

