        for(int i=0; i<size; i++) {
            if(diff[i]>=0) {
                int sum = 0, count = 0;
                while(sum>=0&&count<size) {
                    sum += diff[(i+count)%size];
                    count++;
                }
                if(count==size&&sum>=0) return i;
                else i = i+count-1;
            }
        }
        return -1;
    }
};

