class Solution {
public:
    int canCompleteCircuit(vector<int>& gas, vector<int>& cost) {

        int total=0; //it will give us a difference b/w gas & cos
        int start=0; // and the index of gas station
        int diff=0; // our tank
        for(int i=0;i<gas.size();i++)
        {
            total+=gas[i]-cost[i];// storing values to check that total cost is less than total refill or not
            diff+=gas[i]-cost[i];
            if(diff<0)
            {
                start=i+1;
                diff=0;
            }
        }
        return (total<0)?-1:start;

    }
};
