class Solution {
    func canCompleteCircuit(_ gas: [Int], _ cost: [Int]) -> Int {

        let allgas = gas.reduce(0) { $0 + $1 }, allcost = cost.reduce(0) { $0 + $1 }
        guard allgas >= allcost else { return -1 }

        var value = 0, gasSum = 0, costSum = 0

        for (i, curr) in gas.enumerated() {
            gasSum += curr
            costSum += cost[i]
            if gasSum < costSum {
                value = i + 1
                gasSum = 0
                costSum = 0
            }
        }
        return value
    }
}
