class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) {
        int n = gas.length;
        int prevRemainingGas = 0;
        int remainingGas = 0;
        int possibleStart = 0;
        for (int i = 0; i < n; ++i) {
            int curGas = gas[i];
            int curCost = cost[i];
            remainingGas += curGas - curCost;
            if (remainingGas < 0) {
                possibleStart = i + 1;
                prevRemainingGas += remainingGas;
                remainingGas = 0;
            }
        }
        return possibleStart == n ? -1 : remainingGas + prevRemainingGas >= 0 ? possibleStart : -1;
    }
}
