class Solution {
    public int canCompleteCircuit(int[] gas, int[] cost) {
        int n = gas.length;
        int balance = 0;
        int negativeSum = 0;
        int possibleStart = 0;

        for (int i = 0; i < n; ++i) {
            balance += gas[i] - cost[i];
            if (balance < 0) {
                negativeSum += balance;
                possibleStart = i + 1;
                balance = 0;
            }
        }

        if (possibleStart == n) {
            return -1;
        }
        if (balance >= Math.abs(negativeSum)) return possibleStart;
        return -1;
    }
}
